import React,{ useState,useEffect, useRef } from 'react';
import { Form, Input, Select ,Tag ,TimePicker ,Button } from 'antd';
import { useDispatch, useSelector } from 'react-redux';
/* actions */
import { setEn,setZh } from '../state/reducer';
// import { saveItem } from '../../../State/action';

/* lodash */
import _ from 'lodash';

/* moment */
import moment from 'moment-timezone';
moment.locale('zh-cn',{ weekdays: '星期日_星期一_星期二_星期三_星期四_星期五_星期六'.split('_'), });

const { Option } = Select;

export default function ModalBox () {
  const language = useSelector(state=>state.language.lang);
  const tags = useSelector(state=>state.restaurant.tags);
  const selectedList = useSelector(state=>state.restaurant.selectedItem);

  const dispatch = useDispatch();

  const [ inputValue,setInputValue ] = useState(selectedList.newName);
  const [ time,setTime ] = useState(new Date());
  const [ tagList,setTagList ] = useState([]);

  var newYork = moment.tz(time, 'America/New_York').format('YYYY-MM-DD HH:mm:ss dddd');
  const { RangePicker } = TimePicker;

  /* 纽约时间 */
  const inputRef = useRef();

  /* 改变语言 */
  async function changeLang (value){
    if(value == 'zh-CN'){
      await dispatch(setZh());
    }else{
      await dispatch(setEn());
    }
    setInputValue(selectedList.name[`${language}`]);
  }

  useEffect(()=>{
    setTagList(selectedList.tags);
  },[ selectedList.tags ]);

  useEffect(()=>{
    let timer = setInterval(()=>{
      setTime(new Date());
    },1000);
    return ()=>{
      if (timer){
        clearInterval(timer);
      }
    };
  },[]);

  useEffect( async ()=>{

    await setInputValue(selectedList.name[`${language}`]);

    inputRef.current.state.value = inputValue;

  },[ language,inputValue ]);

  /* 渲染所有的tags列表 */
  function renderTagList (){
    return _.map(tags,(item)=>{
      return (
        <Option value={ item } key={ Math.random() }>{item}</Option>
      );
    });
  }
  /* 增加tags */
  function changeTag (value){
    let cloneList = _.cloneDeep(tagList);
    if(_.indexOf(cloneList,value) == -1){
      cloneList.push(value);
    }
    setTagList(cloneList);
  }
  /*删除tags */
  function close (index){
    let cloneList = _.cloneDeep(tagList);
    cloneList.splice(index,1);
    setTagList(cloneList);
  }

  /* 渲染餐馆的tags */
  function renderTags (){
    return tagList.map((tag,index) => {
      let color = tag.length > 5 ? 'geekblue' : 'green';
      if (tag === 'loser') {
        color = 'volcano';
      }
      return (
        <Tag color={ color } key={ Math.random() } closable onClose={ ()=>{close(index);} } style={{ marginRight :'10px' }}>
          {tag.toUpperCase()}
        </Tag>
      );
    });}

  /* 时间选择 */
  function renderTimeList (){
    let array = [ '星期一','星期二','星期三','星期四','星期五','星期六','星期天' ];
    function formatTime (value){
      return `${value / 60}:${value % 60}:00`;
    }
    return _.map(array,(item,index)=>{
      let hourInfo = selectedList.hours[index];
      return (
        <div key={ item }>
          <Button type="primary" disabled>
            {item}
          </Button>
          {hourInfo ?
            <RangePicker value={ [ moment( formatTime(hourInfo.start),'HH:mm:ss'),moment(formatTime(hourInfo.end), 'HH:mm:ss') ] }></RangePicker> :
            <RangePicker value={ [ moment('00:00:00', 'HH:mm:ss'),moment('00:00:00', 'HH:mm:ss') ] }></RangePicker>}
        </div>
      );
    });
  }

  return (
    <div>
      <Form >
        <Form.Item label="餐馆名称：">
          <Input.Group compact>
            <Form.Item
              name={ [ 'name', 'province' ] }
              noStyle
              rules={ [ { required: true, message: 'Province is required' } ] }
            >
              <Select style={{ width :'80px' }} defaultValue={ language } onChange={ (value)=>{changeLang(value); } }>
                <Option value="zh-CN" >中文</Option>
                <Option value="en-US" >英文</Option>
              </Select>
            </Form.Item>

            <Form.Item
              name={ [ 'address', 'street' ] }
              noStyle
              rules={ [ { required: true, message: 'Street is required' } ] }
            >
              <Input style={{ width : '50%' }} placeholder="Input value" ref={ inputRef }  />
            </Form.Item>
          </Input.Group>
        </Form.Item>
        <Form.Item label="餐馆标签：">
          <Form.Item>
            <Select style={{ width :'120px',margin :'0 10px 6px 0' }} defaultValue={ tags[0] } onChange={ (value)=>{changeTag(value);} }>
              {renderTagList()}
            </Select>
            {renderTags()}
          </Form.Item>
        </Form.Item>
        <Form.Item label="开门时间：">
          <Form.Item>
            <h3>纽约当地时间</h3>
            <h1 >{newYork}</h1>
            {renderTimeList()}
          </Form.Item>
        </Form.Item>
      </Form>
    </div>
  );
}
