      //       }
      //       return (
      //         <Tag color={ color } key={ tag }>
      //           {tag.toUpperCase()}
      //         </Tag>
      //       );
      //     })}
      //   </>
      // ),
    },
    {
      title: '操作',
      key: 'action'
    },
    {
      title: '手动关闭',
      key: 'closed'
    },
  ];
  return (
    <div>
      <Table dataSource={ dataSource } columns={ columns } />;
    </div>
  );
}
