import React, { useState } from 'react';
import { Form, Input, Select } from 'antd';
// import { setEn,setZh } from '../state/reducer';
// import { useDispatch, useSelector } from 'react-redux';

const { Option } = Select;

export default function ModalBox (item) {
  // const language = useSelector(state=>state.language.lang);
  // const dispatch = useDispatch();
  const [ value,setValue ] = useState('zh-CN');
  return (
    <div>
      <Form.Item label="Address">
        <Input.Group compact>
          <Form.Item
            name={ [ 'address', 'province' ] }
            noStyle
            rules={ [ { required: true, message: 'Province is required' } ] }
          >
            <Select style={{ width :'80px' }}  onChange={ ()=>{console.log(value);} }>
              <Option value="zh" onChange={ ()=>{setValue('zh-CN');} }>中文</Option>
              <Option value="en" onChange={ ()=>{setValue('en-US');} }>英文</Option>
            </Select>
          </Form.Item>
          <Form.Item
            name={ [ 'address', 'street' ] }
            noStyle
            rules={ [ { required: true, message: 'Street is required' } ] }
          >
            <Input style={{ width : '50%' }} placeholder="Input street" value={ item.name }/>
          </Form.Item>
        </Input.Group>
      </Form.Item>
    </div>
  );
}
