import React,{ useState,useEffect } from 'react';
import { Form, Input, Select } from 'antd';
import { setEn,setZh } from '../state/reducer';
import { useDispatch, useSelector } from 'react-redux';
// import {getStorage} from '../../../../../Common/utils'

const { Option } = Select;

export default function ModalBox (info) {
  const language = useSelector(state=>state.language.lang);
  // console.log(info.perItem);
  const dispatch = useDispatch();
  const [ inputValue,setInputValue ] = useState('');

  function changeLang (value){
    if(value == 'zh-CN'){
      dispatch(setZh());
    }else{
      dispatch(setEn());
    }
  }

  useEffect( ()=>{
    // let text = info.perItem.newName;
    if(info.perItem.name){
      setInputValue(info.perItem.name[`${language}`]);
      console.log(inputValue);
    }
  },[ language ]);
  return (
    <div>
      <Form.Item label="Address">
        <Input.Group compact>
          <Form.Item
            name={ [ 'address', 'province' ] }
            noStyle
            rules={ [ { required: true, message: 'Province is required' } ] }
          >
            <Select style={{ width :'80px' }} defaultValue={ language }onChange={ (value)=>{changeLang(value); } }>
              <Option value="zh-CN" >中文</Option>
              <Option value="en-US" >英文</Option>
            </Select>
          </Form.Item>
          <Form.Item
            name={ [ 'address', 'street' ] }
            noStyle
            rules={ [ { required: true, message: 'Street is required' } ] }
          >
            <Input style={{ width : '50%' }} placeholder="Input value" Value={ inputValue }  onChange={ (e)=>{setInputValue(e.target.value);} }/>
          </Form.Item>
        </Input.Group>
      </Form.Item>
    </div>
  );
}
