import React, { useEffect, useRef } from 'react';
/* style */
import './order.scss'
/* antd */
import { DatePicker, Space } from 'antd';
import { useSelector } from 'react-redux';

/* lodash */
import _ from 'lodash'
/* echarts */
 // 引入 ECharts 主模块
import echarts from 'echarts/lib/echarts';
// 引入环形图
import 'echarts/lib/chart/pie';
// 引入提示框和标题组件
import 'echarts/lib/component/tooltip';
import 'echarts/lib/component/title';
import 'echarts/lib/component/visualMap';

/* actions */
import {getOrder} from './state/reducer'
import { useDispatch } from 'react-redux';

const { RangePicker } = DatePicker;
export default function Order () {
  const orderList=useSelector(state=>state.order.list)
  const dispatch=useDispatch()
  // const [peopleData,setPeopleData]=useState({})
  const peopleRef =useRef()
  /* eslint-disable */

  useEffect(async()=>{
    console.log(peopleRef.current)
    let result=await dispatch(getOrder('2020-10-29T08:28:34.125Z','2020-11-05T08:28:34.125Z'))
    // initCharts()
  },[])
  useEffect(()=>{
    // console.log(orderList)
    if(orderList.length>0){
      let map = new Map()
      let array = []
      _.map(orderList,(item)=>{
        if(item.user){
          array.push(item.user)
        }
      })
      console.log(array)
      let list=[]
      _.forIn(_.groupBy(array,'username'),(value,key)=>{
        let item = {
          title:'',
          count : 0
        };
        item.title = key.toString();
        item.count = value.length;
      list.push(item);
    })
    // console.log(list)
    setPeopleData(list)
    initCharts()

    }
  },[orderList.length])

  // useEffect(()=>{
  //   console.log(peopleData)
  //   _.map(peopleData,(item)=>{
  //     console.log(item)
  //   })
  // },[peopleData])

  function initCharts(){
    console.log('orderList==>',orderList)
    var piedata = _.map(peopleData,(item) => {
      return {
        value: item.title,   //value和name是echarts规定的，呜呜呜
        name: item.count,
      };
    });
    console.log(piedata)
    var myChart = echarts.init(document.getElementById('order-people'));
  myChart.setOption({
    // tooltip: {
    //   trigger: 'item',
    //   // formatter: "{a} <br/>{b}: {c} ({d}%)"
    //   // formatter: '{b}:   {d}%',
    //   formatter: '{b}: {c} ({d}%)',
    // },
    series: [
      {
        name: '订单人群',
        type: 'pie',
        radius: '55%',
        // label: {
        //   formatter: '{b}: {c} ({d}%)',
        //   textStyle: {
        //     color: '#000000',
        //     fontSize: 12,
        //   },
        // },
        // data: piedata,
        data:[
          {value:235, name:'视频广告'},
          {value:274, name:'联盟广告'},
          {value:310, name:'邮件营销'},
          {value:335, name:'直接访问'},
          {value:400, name:'搜索引擎'}
        ]
      },]
  });
}

  return (
    <div>
      <Space direction="vertical" size={ 20 }>
        <RangePicker style={{ width :'800px' }}/>
      </Space>
      <div className='main'>
        <div className='order-quantity' ></div>
        <div id='order-people' ref={peopleRef}></div>
      </div>
    </div>
  );
}
