import React, { useEffect,useState } from 'react';
import PropTypes from 'prop-types';

/* antd */
import { Select,Table,Switch ,Input, Button, Space } from 'antd';
import Highlighter from 'react-highlight-words';
import { SearchOutlined } from '@ant-design/icons';

// import reqwest from 'reqwest';

const { Option } = Select;
import _ from 'lodash';
import { useSelector, useDispatch } from 'react-redux';
import { getRest } from '../../State/reducer';
import { getMenu } from './state/reducer';

/* table 数据 */

export default function Menu () {
  const restList = useSelector(state=>state.restaurant.list);
  const foodList = useSelector(state=>state.menu.list);
  const [ pagination/* ,setPagination */ ] = useState({
    page: 1,
    pageSize: 10,
  });
  const dispatch = useDispatch();
  const [ searchText,setSearchText ] = useState('');
  const [ searchedColumn,setSearchedColumn ] = useState('');

  let handleTableChange = (pagination, filters, sorter) => {
    this.fetch({
      sortField: sorter.field,
      sortOrder: sorter.order,
      pagination,
      ...filters,
    });
  };

  let getColumnSearchProps = (dataIndex) => ({
    filterDropdown: ({ setSelectedKeys, selectedKeys, confirm, clearFilters }) => (
      <div style={{ padding : 8 }}>
        <Input
          ref={ node => {
            this.searchInput = node;
          } }
          placeholder={ `Search ${dataIndex}` }
          value={ selectedKeys[0] }
          onChange={ e => setSelectedKeys(e.target.value ? [ e.target.value ] : []) }
          onPressEnter={ () => handleSearch(selectedKeys, confirm, dataIndex) }
          style={{ width : 188, marginBottom : 8, display : 'block' }}
        />
        <Space>
          <Button
            type="primary"
            onClick={ () => handleSearch(selectedKeys, confirm, dataIndex) }
            icon={ <SearchOutlined /> }
            size="small"
            style={{ width : 90 }}
          >
            Search
          </Button>
          <Button onClick={ () =>handleReset(clearFilters) } size="small" style={{ width : 90 }}>
            Reset
          </Button>
        </Space>
      </div>
    ),
    filterIcon: filtered => <SearchOutlined style={{ color : filtered ? '#1890ff' : undefined }} />,
    onFilter: (value, record) =>
      record[dataIndex]
        ? record[dataIndex].toString().toLowerCase().includes(value.toLowerCase())
        : '',
    onFilterDropdownVisibleChange: visible => {
      if (visible) {
        setTimeout(() => this.searchInput.select(), 100);
      }
    },
    render: text =>
      searchedColumn === dataIndex ? (
        <Highlighter
          highlightStyle={{ backgroundColor: '#ffc069', padding: 0 }}
          searchWords={ [ searchText ] }
          autoEscape
          textToHighlight={ text ? text.toString() : '' }
        />
      ) : (
        text
      ),
  });

  let handleSearch = (selectedKeys, confirm, dataIndex) => {
    confirm();
    setSearchText(selectedKeys[0]);
    setSearchedColumn(dataIndex);
  };

  let handleReset = clearFilters => {
    clearFilters();
    setSearchText('');
  };

  useEffect(async ()=>{
    await dispatch(getRest());
    // test();
  },[ restList.length ]);

  /* 渲染餐馆名字 */
  function renderNameOptions (){
    return _.map(restList,(item)=>{
      return (
        <Option value={ item._id } key={ Math.random() }>{item.name['zh-CN']}</Option>
      );
    });
  }
  /* 选定餐馆 */
  async function onChange (value) {
    console.log(`selected ${value}`);
    await dispatch(getMenu(value,1,10,''));
  }

  function onSearch (val) {
    console.log('search:', val);
  }
  const columns = [
    {
      title: '菜品',
      dataIndex: 'name',
      render: name => name['zh-CN'],
      width: '40%',
      ...getColumnSearchProps('name'),
    },
    {
      title: '价格',
      dataIndex: 'price',
      // sorter: true,
      render: price => <span> ${(price / 100).toFixed(2)}</span>,
      width: '30%',
    },
    {
      title: '状态',
      key: 'available',
      render:(text,record)=> {
        return (
          <Switch checked={ record.available }/>
        );
      },
      width: '30%',

    },
  ];
  return (
    <div>
      <Select
        showSearch
        style={{ width : 200 }}
        placeholder="Select a person"
        optionFilterProp="children"
        onChange={ onChange }
        onSearch={ onSearch }
        filterOption={ (input, option) =>{
          return option.children.toLowerCase().indexOf(input.toLowerCase()) >= 0;
        }
        }
      >
        {renderNameOptions()}
      </Select>
      <Table columns={ columns }
        dataSource={ foodList }
        pagination={ pagination }
        onChange={ handleTableChange }/>
    </div>
  );

}

Menu.PropTypes = {
  setSelectedKeys:PropTypes.func,
  selectedKeys: PropTypes.number,
  confirm: PropTypes.func,
  clearFilters: PropTypes.func
};
