import React,{ useState,useEffect } from 'react';
import { Form, Input, Select } from 'antd';
import { setEn,setZh } from '../state/reducer';
import { useDispatch, useSelector } from 'react-redux';
// import {getStorage} from '../../../../../Common/utils'

const { Option } = Select;

export default function ModalBox (info) {
  const language = useSelector(state=>state.language.lang);
  // console.log(info.perItem);
  const dispatch = useDispatch();
  const [ inputValue,setInputValue ] = useState(info.perItem.name[`${language}`]);

  function changeLang (value){
    if(value == 'zh-CN'){
      dispatch(setZh());
    }else{
      dispatch(setEn());
    }
    setInputValue(info.perItem.name[`${language}`]);
  }

  useEffect( ()=>{
    changeInput(info.perItem.newName);
  },[ language ]);

  useEffect(()=>{
    if(inputValue){
      console.log(inputValue);
      // changeInput(inputValue);
    }
  },[ inputValue ]);

  function changeInput (value){
    setInputValue(value);
  }

  return (
    <div>
      <Form.Item label="餐馆名称：">
        <Input.Group compact>
          <Form.Item
            name={ [ 'name', 'province' ] }
            noStyle
            rules={ [ { required: true, message: 'Province is required' } ] }
          >
            <Select style={{ width :'80px' }} defaultValue={ language } onChange={ (value)=>{changeLang(value); } }>
              <Option value="zh-CN" >中文</Option>
              <Option value="en-US" >英文</Option>
            </Select>
          </Form.Item>
          <Form.Item
            name={ [ 'address', 'street' ] }
            noStyle
            rules={ [ { required: true, message: 'Street is required' } ] }
          >
            <Input style={{ width : '50%' }} placeholder="Input value" defaultValue={ inputValue } onChange={ ()=>{changeInput(); console.log('1');} }/>
          </Form.Item>
        </Input.Group>
      </Form.Item>
    </div>
  );
}
