import React, { useEffect, useState } from 'react';

/* antd */
import { Select,Table,Switch } from 'antd';

// import reqwest from 'reqwest';

const { Option } = Select;
import _ from 'lodash';
import { useSelector, useDispatch } from 'react-redux';
import { getRest } from '../../State/reducer';
import { getMenu } from './state/reducer';

/* table 数据 */
const columns = [
  {
    title: '菜品',
    dataIndex: 'name',
    filters: [
      { text: 'Male', value: 'male' },
      { text: 'Female', value: 'female' },
    ],
    width: '20%',
  },
  {
    title: '价格',
    dataIndex: 'price',
    // sorter: true,
    render: price => price,
    width: '20%',
  },
  {
    title: '状态',
    key: 'available',
    render:(text,record)=> {
      return (
        <Switch defaultChecked />
      );
    }
  },
];
const getRandomuserParams = params => {
  return {
    results: params.pagination.pageSize,
    page: params.pagination.current,
    ...params,
  };
};
export default function Menu () {
  const restList = useSelector(state=>state.restaurant.list);
  // const [ page,setPage ] = useState(1);
  const dispatch = useDispatch();
  console.log(restList);

  useEffect(async ()=>{
    await dispatch(getRest());
    // test();
  },[ restList.length ]);

  /* 渲染餐馆名字 */
  function renderNameOptions (){
    return _.map(restList,(item)=>{
      return (
        <Option value={ item._id } key={ Math.random() }>{item.name['zh-CN']}</Option>
      );
    });
  }
  /* 选定餐馆 */
  async function onChange (value) {
    console.log(`selected ${value}`);
    await dispatch(getMenu(value,1,10,''));
  }

  function onSearch (val) {
    console.log('search:', val);
  }
  function getColumnSearchProps (dataIndex){
    return({
      filterDropdown: ({ setSelectedKeys, selectedKeys, confirm, clearFilters }) => (
        <div style={{ padding : 8 }}>
          <Input
            ref={ node => {
              this.searchInput = node;
            } }
            placeholder={ `Search ${dataIndex}` }
            value={ selectedKeys[0] }
            onChange={ e => setSelectedKeys(e.target.value ? [ e.target.value ] : []) }
            onPressEnter={ () => this.handleSearch(selectedKeys, confirm, dataIndex) }
            style={{ width : 188, marginBottom : 8, display : 'block' }}
          />
          <Space>
            <Button
              type="primary"
              onClick={ () => this.handleSearch(selectedKeys, confirm, dataIndex) }
              icon={ <SearchOutlined /> }
              size="small"
              style={{ width : 90 }}
            >
             Search
            </Button>
            <Button onClick={ () => this.handleReset(clearFilters) } size="small" style={{ width : 90 }}>
             Reset
            </Button>
          </Space>
        </div>
      ),
      filterIcon: filtered => <SearchOutlined style={{ color : filtered ? '#1890ff' : undefined }} />,
      onFilter: (value, record) =>
        record[dataIndex]
          ? record[dataIndex].toString().toLowerCase().includes(value.toLowerCase())
          : '',
      onFilterDropdownVisibleChange: visible => {
        if (visible) {
          setTimeout(() => this.searchInput.select(), 100);
        }
      },
      render: text =>
        this.state.searchedColumn === dataIndex ? (
          <Highlighter
            highlightStyle={{ backgroundColor: '#ffc069', padding: 0 }}
            searchWords={ [ this.state.searchText ] }
            autoEscape
            textToHighlight={ text ? text.toString() : '' }
          />
        ) : (
          text
        ),
    });
  }
}
