import React, { useEffect } from 'react';
import { Select } from 'antd';

// const { Option } = Select;
import _ from 'lodash';
import { renderOption } from '../../../../Common/utils';
import { useSelector, useDispatch } from 'react-redux';
import { getRest } from '../../State/reducer';

export default function Menu () {
  const restList = useSelector(state=>state.restaurant.list);
  const dispatch = useDispatch();
  console.log(restList);

  useEffect(async ()=>{
    await dispatch(getRest());
    test();
  },[ restList.length ]);
  function onChange (value) {
    console.log(`selected ${value}`);
  }

  function onBlur () {
    console.log('blur');
  }

  function onFocus () {
    console.log('focus');
  }

  function onSearch (val) {
    console.log('search:', val);
  }

  let nameList = _.filter(restList,(o)=>{
    return o.name['zh-CN'];
  });
  function test (){
    let result = _.filter(restList,(o)=>{
      return o.name['zh-CN'];
    });
    console.log(result);
  }
  return (
    <div>
      <Select
        showSearch
        style={{ width : 200 }}
        placeholder="Select a person"
        optionFilterProp="children"
        onChange={ onChange }
        onFocus={ onFocus }
        onBlur={ onBlur }
        onSearch={ onSearch }
        filterOption={ (input, option) =>
          option.children.toLowerCase().indexOf(input.toLowerCase()) >= 0
        }
      >
        {renderOption(nameList)}
      </Select>,
    </div>
  );
}
