import React, {  useEffect,useState } from 'react';
/* antd */
import { Table,Tag,Switch,Modal, Button } from 'antd';

/* request */
import { getRest,saveItem,getTags } from '../../State/action';
/* lodash */
import _ from 'lodash';
import {  useDispatch ,useSelector } from 'react-redux';

/* components */
import ModalBox from './Component/ModalBox';

export default function Restaurant () {
  const [ dataSource,setDataSource ] = useState([]);
  /* 打开modal */
  const [ isClosed,setIsClosed ] = useState(false);
  /* 点击操作之后赋给modal的信息 */
  const [ perItem,setPerItem ] = useState({});
  /* state中存储的餐馆列表 */
  const list = useSelector(state=>state.restaurant.list);
  const language = useSelector(state=>state.language.lang);
  const selectedList = useSelector(state=>state.restaurant.selectedItem);

  const dispatch = useDispatch();

  useEffect(()=>{
    dispatch(getTags());
  },[]);

  useEffect( async ()=>{
    await dispatch(getRest());
    renderList();
  },[ list.length ]);

  useEffect(()=>{
    if(perItem.name){
      setPerItem({ ...perItem,newName:perItem.name[`${language}`] });
    }
  },[ language ]);

  /* 渲染restarant列表 */
  function renderList (){
    let array = [];
    _.map(list,(item)=>{
      array.push({
        name:item.name['zh-CN'],
        address:item.address.formatted,
        tags:item.tags,
        id:item._id,
        modalInfo:item
      });
    });
    console.log(array);
    setDataSource(array);
  }

  /* 人为关闭 */
  function onChange (){
    setIsClosed(!isClosed);
  }

  const [ visible,setVisible ] = useState(false);

  /* 打开操作框 */
  function showModal (value){
    setVisible(true);
    console.log(value);
    setPerItem({ ...value,newName:value.name[`${language}`] });
    dispatch(saveItem(perItem));
  }
  /* 确认 */
  function handleOk (){
    setVisible(false);
  }

  function handleCancel () {
    setVisible(false);
  }
  const columns = [
    {
      title: '餐馆',
      dataIndex: 'name',
      key: 'name'
    },
    {
      title: '地址',
      dataIndex: 'address',
      key: 'address',
    },
    {
      title: '标签',
      key: 'tags',
      dataIndex: 'tags',
      render: tags => (
        <div>
          {tags.map(tag => {
            let color = tag.length > 5 ? 'geekblue' : 'green';
            if (tag === 'loser') {
              color = 'volcano';
            }
            return (
              <Tag color={ color } key={ Math.random() }>
                {tag.toUpperCase()}
              </Tag>
            );
          })}
        </div>
      ),
    },
    {
      title: '操作',
      key: 'action',
      dataIndex: 'index',
      render:( text,record)=>{
        // console.log('record==>',record);
        return (
          <Button type="primary" onClick={ ()=>{showModal(record.modalInfo);} } >
          操作
          </Button>
        );
      }
    },
    {
      title: '手动关闭',
      key: 'closed',
      render:()=> (
        <>
          <Switch  onChange={ onChange } />
        </>
      )
    },
  ];
  return (
    <div>
      <Table dataSource={ dataSource } columns={ columns } />
      <Modal
        title={ perItem.newName }
        visible={ visible }
        onOk={ handleOk }
        onCancel={ handleCancel }
      >
        <ModalBox perItem={ perItem } ></ModalBox>
      </Modal>
    </div>
  );
}
