import { createSlice } from '@reduxjs/toolkit';
import { setStorage,getStorage } from '../../../../../Common/utils';

export const languageSlice = createSlice({
  name: 'counter',
  initialState: {
    lang:getStorage('language') || 'zh-CN',
    isShow:false
  },
  reducers: {
    setZh: (state) => {
      state.lang = 'zh-CN';
      setStorage('language','zh-CN');
    },
    setEn: (state) => {
      state.lang = 'en-US';
      setStorage('language','en-US');
    },
    showModal:(state)=>{
      state.isShow = true;
    },
    hideModal:(state)=>{
      state.isShow = false;
    }
  },
});

export const { setZh ,setEn ,showModal,hideModal } = languageSlice.actions;

// export const getRest = () => async (dispatch )=> {
//   let result = await restaurant();
//   dispatch(saveData(result.list));
//   return result;
// };

export default languageSlice.reducer;
