import React, {  useEffect,useState } from 'react';
/* antd */
import { Table,Tag } from 'antd';

/* request */
import { getRest } from '../../State/action';
/* lodash */
import _ from 'lodash';
import { useDispatch, useSelector } from 'react-redux';

export default function Restaurant () {
  const [ dataSource,setDataSource ] = useState([]);
  const list = useSelector(state=>state.restaurant.list);
  const dispatch = useDispatch();
  useEffect( async ()=>{
    let result = await dispatch(getRest());
    _.map(result.list,(item)=>{
      dataSource.push({
        name:item.name,
        address:item.address,
        tags:item.tags
      });
    });
    setDataSource(dataSource);
  });
  // let dataSource = [];
  // _.forEach(result,(value,key)=>{
  // console.log(item);_.map
  // dataSource.push({
  //   name:item.name,
  //   address:item.address,
  //   tags:item.tags
  // });
  // console.log(dataSource);
  // let obj = { name:item.name,
  //   address:item.address,
  //   tags:item.tags };
  // console.log(obj);
  // return obj;
  // console.log(value,key);
  // });

  // console.log(perItem);

  const columns = [
    {
      title: '餐馆',
      dataIndex: 'name',
      key: 'name'
    },
    {
      title: '地址',
      dataIndex: 'address',
      key: 'address',
    },
    {
      title: '标签',
      key: 'tags',
      dataIndex: 'tags',
      render: tags => (
        <>
          {tags.map(tag => {
            let color = tag.length > 5 ? 'geekblue' : 'green';
            if (tag === 'loser') {
              color = 'volcano';
            }
            return (
              <Tag color={ color } key={ tag }>
                {tag.toUpperCase()}
              </Tag>
            );
          })}
        </>
      ),
    },
    {
      title: '操作',
      key: 'action'
    },
    {
      title: '手动关闭',
      key: 'closed'
    },
  ];
  return (
    <div>
      <Table dataSource={ dataSource } columns={ columns } />;
    </div>
  );
}
