import React, { useEffect, useRef } from 'react';
/* style */
import './order.scss'
/* antd */
import { DatePicker, Space } from 'antd';
import { useSelector } from 'react-redux';

import moment from 'moment'
/* lodash */
import _ from 'lodash'
/* echarts */
 // 引入 ECharts 主模块
import echarts from 'echarts/lib/echarts';
// 引入环形图
import 'echarts/lib/chart/pie';
// 引入提示框和标题组件
import 'echarts/lib/component/tooltip';
import 'echarts/lib/component/title';
import 'echarts/lib/component/visualMap';

/* actions */
import {getOrder} from './state/reducer'
import { useDispatch } from 'react-redux';

const { RangePicker } = DatePicker;
export default function Order () {
  const orderList=useSelector(state=>state.order.list)
  const dispatch=useDispatch()
  const peopleRef =useRef()
  const quantityRef=useRef()
  // const quantityLineRef=useRef()
  // const quantityPieRef=useRef()
  /* eslint-disable */
  useEffect(()=>{
    if(orderList.length>0){
      quantityLine()
      renderPeople()
    }
  },[orderList.length])
  /* 订单量-折线图 */
function quantityLine(){
  let array = []
  _.map(orderList,(item)=>{
    array.push({date:moment(item.createdAt).format('YYYY-MM-DD')})
  })
  // console.log(array)
  let list=handleData(array,'date')
  console.log(list)

  let piedata = _.map(list,(item) => {
    return {
      name: item.name,   //value和name是echarts规定的，呜呜呜
      value: item.count,
    };
  });
  echarts.init(quantityRef.current).setOption({
    title: {
      text: '订单量',
      textStyle: {
        color: '#fff',
        fontSize: 20,
      },
    },
    xAxis: {
      type: 'category',
      data: _.map(piedata,(item)=>{return item.name})
    },
  yAxis: {
      type: 'value'
  },
  series: [
    {
      name: '订单量',
      type: 'line',
      smooth: true,
      data:_.map(piedata,(item)=>{return item.value})
    },
  ]
  })
}
  /* 订单人群 */
function renderPeople(){
  let array = []
  _.map(orderList,(item)=>{
    if(item.user){
      array.push(item.user)
    }
    else{
      array.push(' ')
    }
  })
  let list=handleData(array,'username')
  var piedata = _.map(list,(item) => {
    return {
      name: item.name,   //value和name是echarts规定的，呜呜呜
      value: item.count,
    };
  });
  echarts.init(peopleRef.current).setOption({
    title: {
      text: '订单人群',
      textStyle: {
        color: '#fff',
        fontSize: 20,
      },
    },
    series: [
      {
        name: '订单人群',
        type: 'pie',
        radius: '55%',
        label: {
          formatter: '{b}: {c} ({d}%)',
          textStyle: {
            color: '#fff',
            fontSize: 12,
          },
        },
        data: piedata
      },]
  })
}

function handleData(array,params){
      let list=[]
      console.log(array)
      _.forIn(_.groupBy(array,params),(value,key)=>{
        let item = {
          name:'',
          count : 0
        };
        item.name = key.toString();
        item.count = value.length;
      list.push(item);
    })
    return list
}

async function changeData(date, dateString){
  // console.log(date)
  // console.log(dateString)
  // let start= moment(dateString[0]).utc().toISOString();
  // let end= moment(dateString[1]).utc().toISOString();
  let start= `${dateString[0]}T08:28:34.125Z`;
  let end= `${dateString[1]}T08:28:34.125Z`;
 await dispatch(getOrder(start,end))
}

  return (
    <div>
      <Space direction="vertical" size={ 20 }>
        <RangePicker style={{ width :'800px' }} onChange={changeData}/>
      </Space>
      <div className='main'>
        <div className='order-quantity' ref={quantityRef}>
          {/* <div className='quantity-line' ></div>
          <div className='quantity-pie'  ></div> */}
        </div>
        <div className='order-people' ref={peopleRef}></div>
      </div>
    </div>
  );
}
