import React, { useState } from 'react'
import './login.scss'
/* antd */

import { Form, Input, Button } from 'antd';

/* actions */
import {postLogin} from './state/reducer'
import { useDispatch } from 'react-redux';

const layout = {
  labelCol: { span: 8 },
  wrapperCol: { span: 16 },
};
const tailLayout = {
  wrapperCol: { offset: 8, span: 16 },
};

export default function Login() {
  const [username,setUsername]=useState('')
  const [password,setPassword]=useState('')

const dispatch=useDispatch()
  const onFinish = values => {
    console.log('Success:', values);
  };

  const onFinishFailed = errorInfo => {
    console.log('Failed:', errorInfo);
  };

  async function loginClick(){
    let data={
      username:'admin',
      password:'123456'
    }
    let result=await dispatch(postLogin(data))
    console.log(result)
  }
  return(
    <Form
      {...layout}
      name="basic"
      initialValues={{ remember: true }}
      onFinish={onFinish}
      onFinishFailed={onFinishFailed}
      style={{margin:'300px auto',width:'500px',}}
    >
      <Form.Item
        label="Username"
        name="username"
        rules={[{ required: true, message: 'Please input your username!' }]}
      >
        <Input value={username} onChange={(e)=>{console.log('username');setUsername(e.target.value)}}/>
      </Form.Item>

      <Form.Item
        label="Password"
        name="password"
        rules={[{ required: true, message: 'Please input your password!' }]}
      >
        <Input.Password value={password} onChange={(e)=>{console.log('password');setPassword(e.target.value)}}/>
      </Form.Item>

      <Form.Item {...tailLayout}>
        <Button type="primary" htmlType="submit" onClick={loginClick}>
          登录
        </Button>
      </Form.Item>
    </Form>
      );
}
