import React,{ useState,useEffect, useRef } from 'react';
import { Form, Input, Select } from 'antd';
import { useDispatch, useSelector } from 'react-redux';
/* actions */
import { setEn,setZh } from '../state/reducer';
import { getTags } from '../../../State/action';

/* lodash */
import _ from 'lodash';

const { Option } = Select;

export default function ModalBox (info) {
  const language = useSelector(state=>state.language.lang);
  const tags = useSelector(state=>state.restaurant.tags);
  // console.log(info.perItem);
  const dispatch = useDispatch();
  const [ inputValue,setInputValue ] = useState(info.perItem.name[`${language}`]);
  const inputRef = useRef();

  async function changeLang (value){
    if(value == 'zh-CN'){
      await dispatch(setZh());
    }else{
      await dispatch(setEn());
    }
    setInputValue(info.perItem.name[`${language}`]);
  }

  useEffect(async ()=>{
    await dispatch(getTags());
  },[]);

  useEffect( async ()=>{

    await setInputValue(info.perItem.name[`${language}`]);

    inputRef.current.state.value = inputValue;

  },[ language,inputValue,info.perItem ]);

  function renderTag (){
    return _.map(tags.list,(item)=>{
      return (
        <Option value={ item } key={ Math.random() }>{item}</Option>
      );
    });
  }
  return (
    <div>
      <Form>
        <Form.Item label="餐馆名称：">
          <Input.Group compact>
            <Form.Item
              name={ [ 'name', 'province' ] }
              noStyle
              rules={ [ { required: true, message: 'Province is required' } ] }
            >
              <Select style={{ width :'80px' }} defaultValue={ language } onChange={ (value)=>{changeLang(value); } }>
                <Option value="zh-CN" >中文</Option>
                <Option value="en-US" >英文</Option>
              </Select>
            </Form.Item>

            <Form.Item
              name={ [ 'address', 'street' ] }
              noStyle
              rules={ [ { required: true, message: 'Street is required' } ] }
            >
              <Input style={{ width : '50%' }} placeholder="Input value" ref={ inputRef }  />
            </Form.Item>
          </Input.Group>
        </Form.Item>
        <Form.Item label="餐馆标签：">
          <Input.Group compact>
            <Form.Item
              name={ [ 'tags', 'province' ] }
              noStyle
              rules={ [ { required: true, message: 'Province is required' } ] }
            >
              <Select style={{ width :'100px' }} defaultValue={ tags[0] }>
                {renderTag()}
              </Select>
            </Form.Item>
          </Input.Group>
        </Form.Item>
      </Form>
    </div>
  );
}
