import React, { useEffect, useRef } from 'react';
/* style */
import './order.scss'
/* antd */
import { DatePicker, Space } from 'antd';
import { useSelector } from 'react-redux';

/* lodash */
import _ from 'lodash'
/* echarts */
 // 引入 ECharts 主模块
import echarts from 'echarts/lib/echarts';
// 引入环形图
import 'echarts/lib/chart/pie';
// 引入提示框和标题组件
import 'echarts/lib/component/tooltip';
import 'echarts/lib/component/title';
import 'echarts/lib/component/visualMap';

/* actions */
import {getOrder} from './state/reducer'
import { useDispatch } from 'react-redux';

const { RangePicker } = DatePicker;
export default function Order () {
  const orderList=useSelector(state=>state.order.list)
  const dispatch=useDispatch()
  // const [peopleData,setPeopleData]=useState({})
  const peopleRef =useRef()
  /* eslint-disable */

  // useEffect(async()=>{
  //   console.log(peopleRef.current)
  //   // let result=await dispatch(getOrder('2020-10-31T08:28:34.125Z','2020-11-05T08:28:34.125Z'))
  //   let result=await dispatch(getOrder('2020-10-31','2020-11-05'))

  //   // initCharts()
  // },[])
  useEffect(()=>{
    if(orderList.length>0){

    let list=handleData('username')
    var piedata = _.map(list,(item) => {
      return {
        name: item.title,   //value和name是echarts规定的，呜呜呜
        value: item.count,
      };
    });
    console.log(piedata)
    echarts.init(peopleRef.current).setOption({
    series: [
      {
        name: '订单人群',
        type: 'pie',
        radius: '55%',
        label: {
          formatter: '{b}: {c} ({d}%)',
          textStyle: {
            color: '#fff',
            fontSize: 12,
          },
        },
        data: piedata
      },]
    })
  }
},[orderList.length])

function handleData(params){
      let array = []
      _.map(orderList,(item)=>{
        if(item.user){
          array.push(item.user)
        }else{
          array.push('')
        }
      })
      console.log(array)
      let list=[]
      _.forIn(_.groupBy(array,params),(value,key)=>{
        let item = {
          title:'',
          count : 0
        };
        item.title = key.toString();
        item.count = value.length;
      list.push(item);
    })
    return list
}

async function changeData(date, dateString){
  console.log(date)
  console.log(dateString)
  let start=`${dateString[0]}T08:28:34.125Z`
  let end=`${dateString[1]}T08:28:34.125Z`

  let result=await dispatch(getOrder(start,end))
}

  return (
    <div>
      <Space direction="vertical" size={ 20 }>
        <RangePicker style={{ width :'800px' }} onChange={changeData}/>
      </Space>
      <div className='main'>
        <div className='order-quantity' ></div>
        <div id='order-people' ref={peopleRef}></div>
      </div>
    </div>
  );
}
