import React, { useEffect, useState } from 'react';

/* antd */
import { Select } from 'antd';
import { Table } from 'antd';
import reqwest from 'reqwest';

const { Option } = Select;
import _ from 'lodash';
import { useSelector, useDispatch } from 'react-redux';
import { getRest } from '../../State/reducer';
import { getMenu } from './state/reducer';

/* table 数据 */
const columns = [
  {
    title: '菜品',
    dataIndex: 'name',
    filters: [
      { text: 'Male', value: 'male' },
      { text: 'Female', value: 'female' },
    ],
    width: '20%',
  },
  {
    title: '价格',
    dataIndex: 'price',
    // sorter: true,
    render: price => price,
    width: '20%',
  },
  {
    title: '状态',
    key: 'available',
    render:(text,record)=> {
      return (
      // record.closed !== (null || undefined) ?
        <Switch  onChange={ (checked)=>{onChange(checked,record);} } defaultChecked />
      //  :
      // <Switch  onChange={ (checked)=>{onChange(checked,record);} }  checked={ !_.isEmpty(record.closed) }/>
      );
    }
  },
];
const getRandomuserParams = params => {
  return {
    results: params.pagination.pageSize,
    page: params.pagination.current,
    ...params,
  };
};
export default function Menu () {
  const restList = useSelector(state=>state.restaurant.list);
  // const [ page,setPage ] = useState(1);
  const dispatch = useDispatch();
  console.log(restList);

  useEffect(async ()=>{
    await dispatch(getRest());
    // test();
  },[ restList.length ]);

  /* 渲染餐馆名字 */
  function renderNameOptions (){
    return _.map(restList,(item)=>{
      return (
        <Option value={ item._id } key={ Math.random() }>{item.name['zh-CN']}</Option>
      );
    });
  }
  /* 选定餐馆 */
  async function onChange (value) {
    console.log(`selected ${value}`);
    await dispatch(getMenu(value,1,10,''));
  }

  function onSearch (val) {
    console.log('search:', val);
  }

  return (
    <div>
      <Select
        showSearch
        style={{ width : 200 }}
        placeholder="Select a person"
        optionFilterProp="children"
        onChange={ onChange }
        onSearch={ onSearch }
        filterOption={ (input, option) =>{
          return option.children.toLowerCase().indexOf(input.toLowerCase()) >= 0;
        }
        }
      >
        {renderNameOptions()}
      </Select>
      <Table
        columns={ columns }
        rowKey={ record => record.login.uuid }
        dataSource={ data }
        pagination={ pagination }
        loading={ loading }
        onChange={ this.handleTableChange }
      />
    );
    </div>
  );
}
