import React,{ useState,useEffect, useRef } from 'react';
import { Form, Input, Select } from 'antd';
import { useDispatch, useSelector } from 'react-redux';
/* actions */
import { setEn,setZh } from '../state/reducer';
// import { saveItem } from '../../../State/action';

/* lodash */
import _ from 'lodash';
// import { saveItem } from '../../../State/action';

const { Option } = Select;

export default function ModalBox () {
  const language = useSelector(state=>state.language.lang);
  const tags = useSelector(state=>state.restaurant.tags);
  const selectedList = useSelector(state=>state.restaurant.selectedItem);

  console.log(selectedList);
  const dispatch = useDispatch();
  const [ inputValue,setInputValue ] = useState(selectedList.newName);
  const [ tagList,setTagList ] = useState([]);

  const inputRef = useRef();

  async function changeLang (value){
    if(value == 'zh-CN'){
      await dispatch(setZh());
    }else{
      await dispatch(setEn());
    }
    setInputValue(selectedList.name[`${language}`]);
  }

  useEffect(()=>{
    setTagList(selectedList.tags);
  },[]);

  useEffect( async ()=>{

    await setInputValue(selectedList.name[`${language}`]);

    inputRef.current.state.value = inputValue;

  },[ language,inputValue ]);

  function renderTagList (){
    return _.map(tags,(item)=>{
      return (
        <Option value={ item } key={ Math.random() }>{item}</Option>
      );
    });
  }

  function changeTag (value){
    // console.log(value);
    // let list = selectedList.tags;
    // list.tags.push(value);
    // console.log(list.tags);
    let cloneList = _.cloneDeep(tagList);
    cloneList.push(value);
    setTagList(cloneList);
    // _.map(list,(tag) => {
    //   let color = tag.length > 5 ? 'geekblue' : 'green';
    //   if (tag === 'loser') {
    //     color = 'volcano';
    //   }
    //   return (
    //     <Tag color={ color } key={ Math.random() }>
    //       {tag.toUpperCase()}
    //     </Tag>
    //   );
    // })}
    // dispatch(saveItem({ ...selectedList,tags:list }));
  }
  return (
    <div>
      <Form>
        <Form.Item label="餐馆名称：">
          <Input.Group compact>
            <Form.Item
              name={ [ 'name', 'province' ] }
              noStyle
              rules={ [ { required: true, message: 'Province is required' } ] }
            >
              <Select style={{ width :'80px' }} defaultValue={ language } onChange={ (value)=>{changeLang(value); } }>
                <Option value="zh-CN" >中文</Option>
                <Option value="en-US" >英文</Option>
              </Select>
            </Form.Item>

            <Form.Item
              name={ [ 'address', 'street' ] }
              noStyle
              rules={ [ { required: true, message: 'Street is required' } ] }
            >
              <Input style={{ width : '50%' }} placeholder="Input value" ref={ inputRef }  />
            </Form.Item>
          </Input.Group>
        </Form.Item>
        <Form.Item label="餐馆标签：">
          <Input.Group compact>
            <Form.Item
              name={ [ 'tags', 'province' ] }
              noStyle
              rules={ [ { required: true, message: 'Province is required' } ] }
            >
              <Select style={{ width :'120px' }} defaultValue={ tags[0] } onChange={ (value)=>{changeTag(value);} }>
                {renderTagList()}
              </Select>
            </Form.Item>
          </Input.Group>
        </Form.Item>
      </Form>
    </div>
  );
}
