import React, { useEffect } from 'react';

/* antd */
import { Select,Table,Switch } from 'antd';

// import reqwest from 'reqwest';

const { Option } = Select;
import _ from 'lodash';
import { useSelector, useDispatch } from 'react-redux';
import { getRest } from '../../State/reducer';
import { getMenu } from './state/reducer';

/* table 数据 */
const columns = [
  {
    title: '菜品',
    dataIndex: 'name',
    filters: [
      { text: 'Male', value: 'male' },
      { text: 'Female', value: 'female' },
    ],
    render: name => name['zh-CN'],
    width: '40%',
  },
  {
    title: '价格',
    dataIndex: 'price',
    // sorter: true,
    render: price => <span> ${(price / 100).toFixed(2)}</span>,
    width: '30%',
  },
  {
    title: '状态',
    key: 'available',
    render:(text,record)=> {
      return (
        <Switch checked={ record.available }/>
      );
    },
    width: '30%',

  },
];
const getRandomuserParams = params => {
  return {
    results: params.pagination.pageSize,
    page: params.pagination.page,
    ...params,
  };
};
export default function Menu () {
  const restList = useSelector(state=>state.restaurant.list);
  const foodList = useSelector(state=>state.menu.list);
  const [ pagination,setPagination ] = useState({
    page: 1,
    pageSize: 10,
  });
  const dispatch = useDispatch();

  let handleTableChange = (pagination, filters, sorter) => {
    this.fetch({
      sortField: sorter.field,
      sortOrder: sorter.order,
      pagination,
      ...filters,
    });
  };

  let fetch = (params = {}) => {
    this.setState({ loading: true });
    reqwest({
      url: 'https://randomuser.me/api',
      method: 'get',
      type: 'json',
      data: getRandomuserParams(params),
    }).then(data => {
      console.log(data);
      this.setState({
        loading: false,
        data: data.results,
        pagination: {
          ...params.pagination,
          total: 200,
          // 200 is mock data, you should read it from server
          // total: data.totalCount,
        },
      });
    });
  };
  useEffect(async ()=>{
    await dispatch(getRest());
    // test();
  },[ restList.length ]);

  /* 渲染餐馆名字 */
  function renderNameOptions (){
    return _.map(restList,(item)=>{
      return (
        <Option value={ item._id } key={ Math.random() }>{item.name['zh-CN']}</Option>
      );
    });
  }
  /* 选定餐馆 */
  async function onChange (value) {
    console.log(`selected ${value}`);
    await dispatch(getMenu(value,1,10,''));
  }

  function onSearch (val) {
    console.log('search:', val);
  }

  return (
    <div>
      <Select
        showSearch
        style={{ width : 200 }}
        placeholder="Select a person"
        optionFilterProp="children"
        onChange={ onChange }
        onSearch={ onSearch }
        filterOption={ (input, option) =>{
          return option.children.toLowerCase().indexOf(input.toLowerCase()) >= 0;
        }
        }
      >
        {renderNameOptions()}
      </Select>
      <Table columns={ columns }
        dataSource={ foodList }
        pagination={ pagination }
        onChange={ handleTableChange }/>
    </div>
  );

}
