import React, { useEffect,useState ,useRef } from 'react';

/* antd */
import { Select,Table,Switch ,Input,Space,Button } from 'antd';
const { Option } = Select;
import { SearchOutlined } from '@ant-design/icons';
/* 库 */
import _ from 'lodash';
import pinyinMatch from 'pinyin-match';

import { useSelector, useDispatch } from 'react-redux';
import { getMenu ,getRestaurantName,clearData } from './state/reducer';

export default function Menu () {
  const restNameList = useSelector(state=>state.menu.restaurantNames);
  const foodList = useSelector(state=>state.menu.list);
  const count = useSelector(state=>state.menu.count);

  const [ data,setData ] = useState([]);
  const [ selectedRest,setselectedRest ] = useState('');
  const [ pagination,setPagination ] = useState({
    current:1,
    page: 1,
    pageSize: 10,
    total:count
  });
  const dispatch = useDispatch();
  const searchInput = useRef();
  /* table 数据 */
  const columns = [
    {
      title: '菜品',
      dataIndex: 'name',
      render: name => <span>{name}</span>,
      width: '40%',
      ...getColumnSearchProps('name')
    },
    {
      title: '价格',
      dataIndex: 'price',
      render: price => <span> {price}</span>,
      width: '30%',
    },
    {
      title: '状态',
      key: 'available',
      render:(text,record)=> {
        return (
          <Switch checked={ record.available }/>
        );
      },
      width: '30%',
    },
  ];
  /* 渲染餐馆名称 */
  useEffect(async ()=>{
    await dispatch(clearData());
    await dispatch(getRestaurantName());
  },[]);

  useEffect(()=>{
    console.log(pagination);
    console.log(foodList);
    setPagination({ ...pagination,total:count });
    renderData();
  },[ selectedRest,count ]);

  /* 筛选数据 */
  let getColumnSearchProps = (dataIndex)=>({
    filterDropdown: ({ setSelectedKeys, selectedKeys, confirm, clearFilters }) => (
      <div style={{ padding : 8 }}>
        <Input
          // ref={ node => {
          //   this.searchInput = node;
          // } }
          ref={ searchInput }
          placeholder={ `Search ${dataIndex}` }
          value={ selectedKeys[0] }
          onChange={ e => setSelectedKeys(e.target.value ? [ e.target.value ] : []) }
          onPressEnter={ () => handleSearch(selectedKeys, confirm, dataIndex) }
          style={{ width : 188, marginBottom : 8, display : 'block' }}
        />
        <Space>
          <Button
            type="primary"
            onClick={ () => handleSearch(selectedKeys, confirm, dataIndex) }
            icon={ <SearchOutlined /> }
            size="small"
            style={{ width : 90 }}
          >
            Search
          </Button>
          <Button onClick={ () =>handleReset(clearFilters) } size="small" style={{ width : 90 }}>
            Reset
          </Button>
        </Space>
      </div>
    ),
    filterIcon: filtered => <SearchOutlined style={{ color : filtered ? '#1890ff' : undefined }} />,
    onFilter: (value, record) =>
      record[dataIndex]
        ? record[dataIndex].toString().toLowerCase().includes(value.toLowerCase())
        : '',
    onFilterDropdownVisibleChange: visible => {
      if (visible) {
        setTimeout(() => this.searchInput.select(), 100);
      }
    },
    render: text =>
      this.state.searchedColumn === dataIndex ? (
        <Highlighter
          highlightStyle={{ backgroundColor: '#ffc069', padding: 0 }}
          searchWords={ [ this.state.searchText ] }
          autoEscape
          textToHighlight={ text ? text.toString() : '' }
        />
      ) : (
        text
      ),
  });

  const handleSearch = (selectedKeys, confirm, dataIndex) => {
    confirm();
    this.setState({
      searchText: selectedKeys[0],
      searchedColumn: dataIndex,
    });
  };

  const handleReset = clearFilters => {
    clearFilters();
    this.setState({ searchText: '' });
  };

  /* 渲染餐馆名字 */
  function renderNameOptions (){
    return _.map(restNameList,(item)=>{
      return (
        <Option value={ JSON.stringify(item) } key={ item.id }>{item.name}</Option>
      );
    });
  }
  /* 选定餐馆 */
  async function onChange (value) {
    let info = JSON.parse(value);
    console.log('info',info);
    await dispatch(getMenu(info.id,1,pagination.pageSize,''));
    setPagination({ ...pagination,current:1, page:1,total:count });
    setselectedRest(info.id);
  }

  function onSearch (val) {
    console.log('search:', val);
    // let result = pinyinMatch.match(restNameList.name, val);
    // console.log(result);
  }
  /* 切换页面 */
  async function changePage (value){
    console.log(value);
    await dispatch(getMenu(selectedRest,value.current,value.pageSize,''));
    setPagination({
      ...pagination,
      current:value.current,
      page: value.current,
      pageSize: value.pageSize
    });
    renderData();
  }

  /* 渲染表格数据 */
  function renderData (){
    let data = [];
    _.map(foodList,(item)=>{
      data.push({
        key:item._id,
        name:item.name['zh-CN'],
        price:`$${(item.price / 100).toFixed(2)}`,
        available:item.available
      });
    });
    setData(data);
  }

  useEffect(()=>{
    renderData();
  },[ pagination.page,pagination.pageSize ]);

  return (
    <div>
      <Select
        showSearch
        style={{ width : 200 }}
        placeholder="Select a restaurant"
        optionFilterProp="children"
        onChange={ onChange }
        onSearch={ onSearch }
        filterOption={ (input, option) =>{
          console.log('option child',option.children);
          if(option.children.toLowerCase().indexOf(input.toLowerCase()) >= 0){
            return true;
          }
          else if(pinyinMatch.match(option.children,input )){
            return true;
          }
        }
        }
      >
        {renderNameOptions()}
      </Select>
      <Table columns={ columns }
        dataSource={ data }
        pagination={ pagination }
        onChange={ changePage }
      />
    </div>
  );

}
