import React, { useEffect, useState } from 'react';
/* style */
import './order.scss'
/* antd */
import { DatePicker, Space } from 'antd';
import { useSelector } from 'react-redux';

/* lodash */
import _ from 'lodash'
/* echarts */
 // 引入 ECharts 主模块
// import echarts from 'echarts/lib/echarts';
// 引入环形图
import 'echarts/lib/chart/pie';
// 引入提示框和标题组件
import 'echarts/lib/component/tooltip';
import 'echarts/lib/component/title';
import 'echarts/lib/component/visualMap';

/* actions */
import {getOrder} from './state/reducer'
import { useDispatch } from 'react-redux';

const { RangePicker } = DatePicker;
export default function Order () {
  const orderList=useSelector(state=>state.order.list)
  const dispatch=useDispatch()
  const [peopleData,setPeopleData]=useState({})
  /* eslint-disable */

  useEffect(async()=>{
    let result=await dispatch(getOrder('2020-10-29T08:28:34.125Z','2020-11-05T08:28:34.125Z'))
  },[])
  useEffect(()=>{
    console.log(orderList)
    if(orderList.length>0){
      let map = new Map()
      let array = []
      _.map(orderList,(item)=>{
        if(item.user){
          // console.log(item.user)
          array.push(item.user.username)
        }
      })
      console.log(array)
      array.forEach((item) => {
          //如果map里面没有这个数字进if
          if (!map.get(item)) {
            //新插入一个key-value值，代表第一次遇到这个数字
              map.set(item, 1)
          } else {
              //如果数字已经存在，获取之前计算的次数，然后+1
              map.set(item, Number(map.get(item)) + 1)
          }
      })
      console.log(map.get('value'))
      setPeopleData(map)
    }
  },[orderList.length])

  useEffect(()=>{
    console.log(peopleData)
  },[peopleData])

  // function initCharts(){
  //   console.log('orderList==>',orderList)
  //   var piedata = _.map(orderList,(item) => {
  //     return {
  //       value: item.user.username,   //value和name是echarts规定的，呜呜呜
  //       name: item.x,
  //     };
  //   });
  // }
  // var myChart = echarts.init(document.getElementsByClassName('order-people')[0]);
  // myChart.setOption({
  //   tooltip: {
  //     trigger: 'item',
  //     // formatter: "{a} <br/>{b}: {c} ({d}%)"
  //     // formatter: '{b}:   {d}%',
  //     formatter: '{b}: {c} ({d}%)',
  //   },
  //   series: [
  //     {
  //       name: '订单人群',
  //       type: 'pie',
  //       radius: ['0', '100%'],
  //       label: {
  //         formatter: '{b}: {c} ({d}%)',
  //         textStyle: {
  //           color: '#000000',
  //           fontSize: 12,
  //         },
  //       },
  //       data: piedata,
  //     },]
  // });
  // };
  return (
    <div>
      <Space direction="vertical" size={ 20 }>
        <RangePicker style={{ width :'800px' }}/>
      </Space>
      <div className='main'>
        <div className='order-quantity' ></div>
        <div className='order-people'></div>
      </div>
    </div>
  );
}
