import React, { useEffect,useState } from 'react';
// import PropTypes from 'prop-types';

/* antd */
import { Select,Table,Switch  } from 'antd';
// import Highlighter from 'react-highlight-words';
// import { SearchOutlined } from '@ant-design/icons';

// import reqwest from 'reqwest';

const { Option } = Select;
import _ from 'lodash';
import { useSelector, useDispatch } from 'react-redux';
import { getRest } from '../../State/reducer';
import { getMenu } from './state/reducer';

/* table 数据 */

export default function Menu () {
  const restList = useSelector(state=>state.restaurant.list);
  const foodList = useSelector(state=>state.menu.list);
  const count = useSelector(state=>state.menu.count);
  const [ data,setData ] = useState([]);
  const [ selectedRest,setselectedRest ] = useState('');

  const [ pagination,setPagination ] = useState({
    page: 1,
    pageSize: 10,
  });
  const dispatch = useDispatch();
  // const [ searchText,setSearchText ] = useState('');
  // const [ searchedColumn,setSearchedColumn ] = useState('');

  // let handleTableChange = (pagination, filters, sorter) => {
  //   this.fetch({
  //     sortField: sorter.field,
  //     sortOrder: sorter.order,
  //     pagination,
  //     ...filters,
  //   });
  // };

  useEffect(async ()=>{
    await dispatch(getRest());
    // test();
  },[ restList.length ]);

  /* 渲染餐馆名字 */
  function renderNameOptions (){
    return _.map(restList,(item)=>{
      return (
        <Option value={ item._id } key={ Math.random() }>{item.name['zh-CN']}</Option>
      );
    });
  }
  /* 选定餐馆 */
  async function onChange (value) {
    console.log(`selected ${value}`);
    await dispatch(getMenu(value,1,10,''));
    setselectedRest(value);
  }
  useEffect(()=>{
    let data = [];
    let page = count / setselectedRest.pageSize;
    console.log(page);
    for(let i = 0; i < page; i++){
      _.map(foodList,(item)=>{
        data.push({
          key:item._id,
          name:item.name['zh-CN'],
          price:`${(item.price / 100).toFixed(2)}`,
          available:item.available
        });
      });
    }
    console.log(data);
    setData(data);
  },[ foodList ]);

  function onSearch (val) {
    console.log('search:', val);
  }
  const columns = [
    {
      title: '菜品',
      dataIndex: 'name',
      render: name => <span>{name}</span>,
      width: '40%',
    },
    {
      title: '价格',
      dataIndex: 'price',
      // sorter: true,
      render: price => <span> {price}</span>,
      width: '30%',
    },
    {
      title: '状态',
      key: 'available',
      render:(text,record)=> {
        return (
          <Switch checked={ record.available }/>
        );
      },
      width: '30%',
    },
  ];
  async function changePage (value){
    console.log(value);
    await dispatch(getMenu(selectedRest,value.current,value.pageSize,''));
    setPagination({
      page: value.current,
      pageSize: value.pageSize
    });
  }
  return (
    <div>
      <Select
        showSearch
        style={{ width : 200 }}
        placeholder="Select a person"
        optionFilterProp="children"
        onChange={ onChange }
        onSearch={ onSearch }
        filterOption={ (input, option) =>{
          return option.children.toLowerCase().indexOf(input.toLowerCase()) >= 0;
        }
        }
      >
        {renderNameOptions()}
      </Select>
      <Table columns={ columns }
        // rowKey={ record => record.login.uuid }
        dataSource={ data }
        pagination={ pagination }
        onChange={ changePage }
      />
    </div>
  );

}
