import React ,{ useState }from 'react';
import { Form, Input, Select } from 'antd';
import { setEn,setZh } from '../state/reducer';
import { useDispatch } from 'react-redux';

const { Option } = Select;

export default function ModalBox (item) {
  // const language = useSelector(state=>state.language.lang);
  console.log(item.item.name);
  const dispatch = useDispatch();
  const [ inputValue,setInputValue ] = useState(item.name);

  function changeLang (value){
    if(value == 'zh-CN'){
      dispatch(setZh());
    }else{
      dispatch(setEn());
    }
  }
  return (
    <div>
      <Form.Item label="Address">
        <Input.Group compact>
          <Form.Item
            name={ [ 'address', 'province' ] }
            noStyle
            rules={ [ { required: true, message: 'Province is required' } ] }
          >
            <Select style={{ width :'80px' }}  onChange={ (value)=>{changeLang(value);} }>
              <Option value="zh-CN" >中文</Option>
              <Option value="en-US" >英文</Option>
            </Select>
          </Form.Item>
          <Form.Item
            name={ [ 'address', 'street' ] }
            noStyle
            rules={ [ { required: true, message: 'Street is required' } ] }
          >
            <Input style={{ width : '50%' }} placeholder="Input street" value={ inputValue } onChange={ (e)=>{setInputValue(e.target.value);} }/>
          </Form.Item>
        </Input.Group>
      </Form.Item>
    </div>
  );
}
