import React, {  useEffect,useState } from 'react';
/* antd */
import { Table,Tag,Switch } from 'antd';

/* request */
import { getRest } from '../../State/action';
/* lodash */
import _ from 'lodash';
import {  useDispatch ,useSelector } from 'react-redux';

export default function Restaurant () {
  const [ dataSource,setDataSource ] = useState([]);
  const [ isClosed,setIsClosed ] = useState(false);
  const list = useSelector(state=>state.restaurant.list);
  const dispatch = useDispatch();
  useEffect( async ()=>{
    await dispatch(getRest());
    renderList();
  },[ list.length ]);

  /* 渲染restarant列表 */
  function renderList (){
    let array = [];
    _.map(list,(item)=>{
      array.push({
        name:item.name['zh-CN'],
        address:item.address.formatted,
        tags:item.tags,
        id:item._id
      });
    });
    setDataSource(array);
  }

  function onChange (){
    setIsClosed(!isClosed);
  }
  const columns = [
    {
      title: '餐馆',
      dataIndex: 'name',
      key: 'name'
    },
    {
      title: '地址',
      dataIndex: 'address',
      key: 'address',
    },
    {
      title: '标签',
      key: 'tags',
      dataIndex: 'tags',
      render: tags => (
        <>
          {tags.map(tag => {
            let color = tag.length > 1 ? 'geekblue' : 'green';
            if (tag === 'loser') {
              color = 'volcano';
            }
            return (
              <Tag color={ color } key={ tag }>
                {tag.toUpperCase()}
              </Tag>
            );
          })}
        </>
      ),
    },
    {
      title: '操作',
      key: 'action'
    },
    {
      title: '手动关闭',
      key: 'closed',
      render:()=> (
        <>
          <Switch  onChange={ onChange } />
        </>
      )
    },
  ];
  return (
    <div>
      <Table dataSource={ dataSource } columns={ columns } />
    </div>
  );
}
