import React, { useEffect,useState } from 'react';
/* style */
import './order.scss'
/* antd */
import { DatePicker, Space } from 'antd';
import { useSelector } from 'react-redux';

/* lodash */
import _ from 'lodash'
/* echarts */
 // 引入 ECharts 主模块
// import echarts from 'echarts/lib/echarts';
// 引入环形图
import 'echarts/lib/chart/pie';
// 引入提示框和标题组件
import 'echarts/lib/component/tooltip';
import 'echarts/lib/component/title';
import 'echarts/lib/component/visualMap';

/* actions */
import {getOrder} from './state/reducer'
import { useDispatch } from 'react-redux';

const { RangePicker } = DatePicker;
export default function Order () {
  const orderList=useSelector(state=>state.order.list)
  const dispatch=useDispatch()
  const [peopleData,setPeopleData]=useState({})
  /* eslint-disable */

  useEffect(async()=>{
    let result=await dispatch(getOrder('2020-10-29T08:28:34.125Z','2020-11-05T08:28:34.125Z'))
  },[])
  useEffect(()=>{
    // console.log(orderList)
    if(orderList.length>0){
      let map = new Map()
      let array = []
      _.map(orderList,(item)=>{
        if(item.user){
          array.push(item.user)
        }
      })
      console.log(array)
      let list=[]
      _.forIn(_.groupBy(array,'username'),(value,key)=>{
        let item = {
          title:'',
          count : 0
        };
        item.title = key.toString();
        item.count = value.length;
      list.push(item);
    })
    console.log(list)
    setPeopleData(list)
    }
  },[orderList.length])

  // useEffect(()=>{
  //   console.log(peopleData)
  //   _.map(peopleData,(item)=>{
  //     console.log(item)
  //   })
  // },[peopleData])

  function initCharts(){
    console.log('orderList==>',orderList)
    var piedata = _.map(orderList,(item) => {
      return {
        value: item.user.username,   //value和name是echarts规定的，呜呜呜
        name: item.x,
      };
    });
  }
  var myChart = echarts.init(document.getElementsByClassName('order-people')[0]);
  myChart.setOption({
    tooltip: {
      trigger: 'item',
      // formatter: "{a} <br/>{b}: {c} ({d}%)"
      // formatter: '{b}:   {d}%',
      formatter: '{b}: {c} ({d}%)',
    },
    series: [
      {
        name: '订单人群',
        type: 'pie',
        radius: ['0', '100%'],
        label: {
          formatter: '{b}: {c} ({d}%)',
          textStyle: {
            color: '#000000',
            fontSize: 12,
          },
        },
        data: piedata,
      },]
  });

  return (
    <div>
      <Space direction="vertical" size={ 20 }>
        <RangePicker style={{ width :'800px' }}/>
      </Space>
      <div className='main'>
        <div className='order-quantity' ></div>
        <div className='order-people'></div>
      </div>
    </div>
  );
}
