import React, {  useEffect,useState } from 'react';
/* antd */
import { Table,Tag,Switch,Modal, Button,Form, Input, Select } from 'antd';

/* request */
import { getRest } from '../../State/action';
/* lodash */
import _ from 'lodash';
import {  useDispatch ,useSelector } from 'react-redux';

export default function Restaurant () {
  const [ dataSource,setDataSource ] = useState([]);
  const [ isClosed,setIsClosed ] = useState(false);
  const [ item,setItem ] = useState({});
  const { Option } = Select;
  const list = useSelector(state=>state.restaurant.list);
  const dispatch = useDispatch();
  useEffect( async ()=>{
    await dispatch(getRest());
    renderList();
  },[ list.length ]);

  useEffect(()=>{
    console.log(item);
  },[ item ]);

  /* 渲染restarant列表 */
  function renderList (){
    let array = [];
    _.map(list,(item)=>{
      array.push({
        name:item.name['zh-CN'],
        address:item.address.formatted,
        tags:item.tags,
        id:item._id,
        item:item
      });
    });
    console.log(array);
    setDataSource(array);
  }

  /* 人为关闭 */
  function onChange (){
    setIsClosed(!isClosed);
  }

  const [ visible,setVisible ] = useState(false);

  /* 打开操作框 */
  function showModal (item){
    setVisible(true);
    console.log(item);
    setItem({ name:item.name['zh-CN'] });
  }
  /* 确认 */
  function handleOk (){
    setVisible(false);
  }

  function handleCancel () {
    setVisible(false);
  }
  const columns = [
    {
      title: '餐馆',
      dataIndex: 'name',
      key: 'name'
    },
    {
      title: '地址',
      dataIndex: 'address',
      key: 'address',
    },
    {
      title: '标签',
      key: 'tags',
      dataIndex: 'tags',
      render: tags => (
        <>
          {tags.map(tag => {
            let color = tag.length > 5 ? 'geekblue' : 'green';
            if (tag === 'loser') {
              color = 'volcano';
            }
            return (
              <Tag color={ color } key={ Math.random() }>
                {tag.toUpperCase()}
              </Tag>
            );
          })}
        </>
      ),
    },
    {
      title: '操作',
      key: 'action',
      dataIndex: 'index',
      render:( text,record)=>{
        // console.log('text',text);
        // console.log('record',record);
        // console.log('index',index);

        return (
          <Button type="primary" onClick={ ()=>{showModal(record.item);} }>
          操作
          </Button>
        );
      }
    },
    {
      title: '手动关闭',
      key: 'closed',
      render:()=> (
        <>
          <Switch  onChange={ onChange } />
        </>
      )
    },
  ];
  return (
    <div>
      <Table dataSource={ dataSource } columns={ columns } />
      <Modal
        title={ item.name }
        visible={ visible }
        onOk={ handleOk }
        onCancel={ handleCancel }
      >
        <Form name="complex-form" onFinish={ onFinish } labelCol={{ span: 8 }} wrapperCol={{ span: 16 }}>
          <Form.Item label="Address">
            <Input.Group compact>
              <Form.Item
                name={ [ 'address', 'province' ] }
                noStyle
                rules={ [ { required: true, message: 'Province is required' } ] }
              >
                <Select placeholder="Select province">
                  <Option value="Zhejiang">Zhejiang</Option>
                  <Option value="Jiangsu">Jiangsu</Option>
                </Select>
              </Form.Item>
              <Form.Item
                name={ [ 'address', 'street' ] }
                noStyle
                rules={ [ { required: true, message: 'Street is required' } ] }
              >
                <Input style={{ width : '50%' }} placeholder="Input street" />
              </Form.Item>
            </Input.Group>
          </Form.Item>
        </Form>
      </Modal>
    </div>
  );
}
