import React, { useState } from 'react';
/* style */
import logo from '../../Assets/logo.png';
import './admin.scss';
/* antd */
import { Layout, Menu, Breadcrumb } from 'antd';
import {
  DesktopOutlined,
  PieChartOutlined
} from '@ant-design/icons';
const { Header, Content, Footer, Sider } = Layout;
/* router */
import { BrowserRouter as Router,Switch,Route, Link } from 'react-router-dom';

/* components */
import Restaurant from './Components/Restaurant/Restaurant';
import MyMenu from './Components/Menu/Menu';

export default function Admin () {
  const [ collapsed,setCollapsed ] = useState(false);
  const [ tab,setTab ] = useState('restaurant');
  function onCollapse (){
    setCollapsed(!collapsed );
  }
  function changeTab (tabName){
    setTab(tabName);
  }
  return (
    <Router>
      <div>
        <Layout style={{ minHeight : '100vh' }} >
          <Sider collapsible collapsed={ collapsed } onCollapse={ onCollapse }>
            <div className="logo">
              <img src={ logo } className='logo-img'></img>
            </div>
            <Menu theme="dark" defaultSelectedKeys={ [ '1' ] } mode="inline">
              <Menu.Item key="1" icon={ <PieChartOutlined /> } onClick={ ()=>{changeTab('restaurant');} } >
                <Link to='/admin/restaurant'>
              餐馆
                </Link>
              </Menu.Item>
              <Menu.Item key="2" icon={ <DesktopOutlined /> } onClick={ ()=>{changeTab('menu');} } >
                <Link to='/admin/menu'>
              菜单
                </Link>
              </Menu.Item>
            </Menu>
          </Sider>
          <Layout className="site-layout">
            <Header className="site-layout-background" style={{ padding : 0 }} />
            <Content style={{ margin : '0 16px' }}>
              <Breadcrumb style={{ margin : '16px 0' }}>
                <Breadcrumb.Item key="1" >admin</Breadcrumb.Item>
                <Breadcrumb.Item key="2" >{tab}</Breadcrumb.Item>
              </Breadcrumb>
              <div className="site-layout-background" style={{ padding : 24, minHeight : 360 }}>
                <Switch>
                  <Route path='/admin/restaurant'>
                    <Restaurant></Restaurant>
                  </Route>
                  <Route path='/admin/menu'>
                    <MyMenu></MyMenu>
                  </Route>
                </Switch>
              </div>
            </Content>
            <Footer style={{ textAlign : 'center' }}>Ant Design ©2018 Created by shixt</Footer>
          </Layout>
        </Layout>
      </div>
    </Router>
  );
}
